import React, { Component } from 'react';
import Landing from './Landing'; // do NOT delete!

let credentials = {
    Robert: "TSDqmaPu",
    Isabelle: "ngEyYWTp",
    Guest1: "3fv9fZGL",
    Guest2: "zwvY5Tjt",
    Guest3: "wZXaHrxF",
}

class Login extends Component {

    constructor() {
        super()
        this.state = {
            admin: {
                userName: '',
                password: '',
                rememberMe: false,
                isLoggedIn: false
            }
        };
    }

    handleChange = (event) => {
        const input = event.target;
        const value = input.type === 'checkbox' ? input.checked : input.value;
        
        this.setState({ [input.name]: value });
    };

    componentDidMount() {
        const rememberMe = localStorage.getItem('rememberMe');
        const userName = rememberMe ? localStorage.getItem('userName') : '';
        const password = rememberMe ? localStorage.getItem('password') : '';
        this.setState({ userName, password, rememberMe });
    };

    handleFormSubmit = () => {
        const {isLoggedIn, userName, password, rememberMe } = this.state;
        localStorage.setItem('rememberMe', rememberMe);
        localStorage.setItem('isLoggedIn', isLoggedIn) ;
        localStorage.setItem('userName', rememberMe ? userName: '');
        localStorage.setItem('password', rememberMe ? password : '');
    };


    login = (event) => {
        de
        if (credentials[this.state.userName] === this.state.password) {
            let admin = {
                userName: this.state.userName, 
                password: this.state.password, 
                rememberMe: true, 
                isLoggedIn: true ,
                }
            this.setState({ admin: admin })
            localStorage.admin = JSON.stringify(admin)
            event.preventDefault();
            let username = event.target[0].value
            this.props.updateUser(username)
        }
        else {
            alert('One of the following was not correct')
        }

    }

    loginFuncs = (event) => {
        setTimeout(this.handleFormSubmit(), 5000)
        this.login(event)
    }

    // logout = () => {
    //     let admin = {
    //         user: "",
    //         password: "",
    //         rememberMe: false,
    //         isLoggedIn: false
    //     }
    //     localStorage.admin = JSON.stringify(admin)
    //     this.setState({ admin: admin })
    // }

    render() {
        return (
            <div>
                <form onSubmit={this.loginFuncs} >
                    <label>
                        User: <input name="userName" value={this.state.userName} onChange={this.handleChange} />
                    </label>
                    <label>
                        Password: <input  name="password" type="password" value={this.state.password} onChange={this.handleChange} />
                    </label>
                    <label>
                        <input name="rememberMe" checked={this.state.rememberMe} onChange={this.handleChange} type="checkbox" /> Remember me
                    </label>
                    <button type="submit">Sign In</button>
                </form>
            </div>

        );
    }
}
export default Login
