import React, { Component } from 'react';

class NestInput extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            eggCount: "",
            layTime: "",
            hatchEst: "",
            rehomed: "",
            salvageable: "",
         }
    }

    handleInput = (event) => {
        this.setState({[event.target.name]: event.target.value}, ()=>console.log(this.state))
    }
    addShift = () => {
        this.props.addShift(this.state.firstName, this.state.lastName, this.state.date)
    }

    render() { 
        let input = ["Egg Count", "Laytime", "Estimate Hatch Time", "Rehomed", "Salvageable"]
        return(
            <div className="nest-container">
                <h2>Nest</h2>
                    <div className="nest-component">
                        {input.map((i)=> <div className="new-nest-grid">
                            <span className="new-nest-1"> {i}: </span>
                            <input className="searchInput underline new-nest-2" name={i} onChange={this.handleInput}></input>
                        </div>
                    )}    
                    </div>
            </div> 
        ); 
            
        
    }
}
 
export default NestInput;
